import React from 'react';
import Select from './components/Select';
import Input from './components/Input';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      select: '',
      input0: '',
      input1: '',
      area: '',
      checkbox: '',
    }
  }

  input = (param1, param2) => {
    this.setState(() => ({
      [param1]: param1 === 'checkbox' ? param2.target.checked : param2.target.value,
    }))
  }

  render() {
    return(
      <section>
        <form>
          <Select value={this.state.select} input={this.input}/>
          <Input value={this.state.input0} input={this.input}/>
          <fieldset>
            <label>
              text area
              <br></br>
              <textarea value={this.state.area} onChange={(event) => this.input('area', event)}></textarea>
            </label>
          </fieldset>
          <fieldset>
            <label>Testando esse checkbox</label>
            <input type="checkbox" onChange={(event) => this.input('checkbox', event)} />
          </fieldset>
        </form>
      </section>
    )
  }
}

export default Form;
