import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.inputHandler = this.inputHandler.bind(this);
    this.state = {
      input0: '',
      input1: '',
    }
  }

  inputHandler = (state) => (this.setState((event) => ({[state]: event.target.value,})))

  render() {
    return(
      <section>
        <form>
          <label>
            selecione
            <select></select>
          </label>
          <label>
            inputs
            <input className='input0' onChange={ () => this.inputHandler('input0') } />
            <input className='input1' onChange={ () => this.inputHandler('input1') } />
          </label>
          <label>
            text area
            <textarea></textarea>
          </label>
        </form>
      </section>
    )
  }
}

export default Form;
