import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      input0: '',
      input1: '',
    }
  }

  input0 = (event) => {
    this.setState(() => ({
      input0: event.target.value
    }))
  }

  input1 = (event) => {
    this.setState(() => ({
      input1: event.target.value
    }))
  }

  render() {
    return(
      <section>
        <form>
          <label>
            selecione
            <select>
              <option>abc</option>
              <option>def</option>
              <option>ghi</option>
            </select>
          </label>
          <label>
            inputs
            <input className='input0' value={this.state.input0} onChange={this.input0}/>
            <input className='input1' value={this.state.input1} onChange={this.input1}/>
          </label>
          <label>
            text area
            <textarea></textarea>
          </label>
        </form>
      </section>
    )
  }
}

export default Form;
