import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.teste = this.teste.bind(this);
    this.state = {
      input0: '',
      input1: '',
    }
  }

  teste(state) {
    this.setState((event) => ({
      [state]: event.target.value
    }))
  }

  render() {
    return(
      <section>
        <form>
          <label>
            selecione
            <select></select>
          </label>
          <label>
            inputs
            <input className='input0' value={this.state.input0} onChange={() => this.teste('input0')}/>
            <input className='input1' value={this.state.input1} onChange={() => this.teste('input1')}/>
          </label>
          <label>
            text area
            <textarea></textarea>
          </label>
        </form>
      </section>
    )
  }
}

export default Form;
