import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      select: '',
      input0: '',
      input1: '',
      area: '',
      checkbox: true,
    }
  }

  input = (param1, param2) => {
    const valor = param1 === 'checkbox' ? param2.target.checked : param2.target.value
    this.setState(() => ({
      [param1]: valor
    }))
  }

  render() {
    return(
      <section>
        <form>
          <label>
            selecione
            <select value={this.state.select} onChange={(event) => this.input('select', event)}>
              <option>abc</option>
              <option>def</option>
              <option>ghi</option>
            </select>
          </label>
          <label>
            inputs
            <input className='input0' value={this.state.input0} onChange={(event) => this.input('input0', event)}/>
            <input className='input1' value={this.state.input1} onChange={(event) => this.input('input1', event)}/>
          </label>
          <label>
            text area
            <textarea value={this.state.area} onChange={(event) => this.input('area', event)}></textarea>
          </label>
          <input type="checkbox" onChange={(event) => this.input('checkbox', event)}/>
        </form>
      </section>
    )
  }
}

export default Form;
