import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      select: '',
      input0: '',
      input1: '',
      area: ''
    }
  }

  select = (event) => {
    this.setState(({ select: event.target.value}))
  }

  input0 = (param1, param2) => {
    this.setState(() => ({
      [param1]: param2.target.value
    }))
  }

  input1 = (event) => {
    this.setState(() => ({
      input1: event.target.value
    }))
  }

  area = (event) => {
    this.setState(({ area: event.target.value}))
  }

  render() {
    return(
      <section>
        <form>
          <label>
            selecione
            <select value={this.state.select} onChange={this.select}>
              <option>abc</option>
              <option>def</option>
              <option>ghi</option>
            </select>
          </label>
          <label>
            inputs
            <input className='input0' value={this.state.input0} onChange={this.input0}/>
            <input className='input1' value={this.state.input1} onChange={this.input1}/>
          </label>
          <label>
            text area
            <textarea value={this.state.area} onChange={this.area}></textarea>
          </label>
        </form>
      </section>
    )
  }
}

export default Form;
