import React from 'react';

class Submit extends React.Component{
  render() {
    const { displa, state } = this.props;
    const entries = Object.entries(state).map((estado) => {
      if (estado[0] === 'display') estado[0] = ''
      if (estado[0] === 'alerted') estado[0] = ''
      return estado;
    })
    return(
        <button
          type='submit'
          className='btnSubmit'
          onClick={displa}
          style={{ display: state.display === 'none' ? 'flex' : 'none'  }}
          disabled={entries > 9 ? true : false}
        >
          {console.log(entries)}
          Enviar
        </button>
    )
  }
}

export default Submit;
