import React from "react";

class FormName extends React.Component{
  toUpperCase = (event) =>{
    event.target.value.toUpperCase();
  }

  render() {
    return(
      <div>
        <label>
          Nome:
          <br></br>
          <input type="text" maxlength="40" isRequired value={this.toUpperCase}>
          </input>
        </label>
      </div>
    )
  }
}

export default FormName;
