import React from 'react';
import FormName from './FormName';

class Form extends React.Component{
  constructor() {
    super();
    this.state= {
      name:'',
      email:'',
    }
  }

  handler = (event) => {
    this.setState({ name: event.target.value.toUpperCase()})
  };

  render() {
    return (
      <form>
          <FormName name={this.state.name} handler={this.handler}/>
      </form>
    );
  }
}

export default Form;
