import React from 'react';
import './App.css';
import FormName from './components/FormName';

class App extends React.Component {
  constructor() {
    super();
    this.state= {
      Name:'',
    }
  }

  toUpper = (event) => event.target.value.toUpperCase();

  render() {
    return (
      <form className='App-header'>
        <fieldset>
          <FormName handler={this.toUpper}/>
        </fieldset>
      </form>
    );
  }
}

export default App;
