import { useEffect, useState } from "react";

const DELAY = 1000;

function Timer() {
 const [tempo, setTempo] = useState(1);

 const timerInterval = () => setTempo((before) => before + 1);

 useEffect(() => {
  tempo === 10 && setTempo(1)
  const tick = setInterval(timerInterval, DELAY);
  return () => clearInterval(tick)
 }, [tempo]);

 return { tempo }
}

export default Timer;
