import { useEffect, useState } from "react";

const DELAY = 1000;

function useTimer() {
 const [tempo, setTempo] = useState(1);
 const [random, setRandom] = useState(1);

 const timerInterval = () => setTempo((before) => before + 1);
 const randomizing = () => setRandom(Math.floor(Math.random() * 101) )

 useEffect(() => {
  tempo === 11 && setTempo(1) && randomizing();
  const tick = setInterval(timerInterval, DELAY);
  return () => clearInterval(tick);
 }, [tempo, random]);

 return { tempo, random }
}

export default useTimer;
