import { useEffect, useState } from "react";

const DELAY = 1000;

const INITITAL = {
  tempo: 1,
  random: 1,
}

function useTimer() {
 const [tempo, setTempo] = useState(INITITAL);

 const timerInterval = () => setTempo((before) => ({
  ...before,
  tempo: before.tempo + 1,
 }));

 useEffect(() => {
  tempo === 11 && setTempo(1);
  const tick = setInterval(timerInterval, DELAY);
  return () => clearInterval(tick);
 }, [tempo]);

 return { tempo }
}

export default useTimer;
